#include <bits/stdc++.h>using namespace std; void solve() {    int a, b;    cin >> a >> b;    if (b % a) cout << (b + a) / a << '\n';    else cout << b / a << '\n';} signed main(void){        std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     // cin >> t;    while (t--) solve();    return 0;    }