#include <bits/stdc++.h>using namespace std; void solve() {    int a, b, c, ans = 0;    cin >> a >> b >> c;    for (int i = 2; i <= 100; i++){        if (i <= b && i - 1 <= a && i + 1 <= c){            ans = 3 * i;        }    }    cout << ans << '\n';} signed main(void){          std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     // cin >> t;    while (t--) solve();    return 0;    }