#include <stdio.h> int main(void){    int t;    scanf ("%d", &t);    while (t--){        int n;        scanf ("%d", &n);        if (n % 7 == 0){            printf ("%d\n", n);        }        else{            if (!(n / 10 == (n-n%7)/10)){                printf ("%d\n", n + 7 - (n%7));            }            else if (n - n%7 == 7){                printf ("14\n");            }            else{                printf ("%d\n", n - (n%7));            }        }    }}