#include <bits/stdc++.h>using namespace std; void solve() {    int n, q;    cin >> n >> q;    int a[n + 1], b[n];    for (int i = 0; i < n; i++){        cin >> a[i + 1];    }    for (int i = 0; i < n; i++){        cin >> b[i];        a[i + 1] = max (a[i + 1], b[i]);    }    for (int i = n - 1; i > 0; i--){        a[i] = max (a[i], a[i + 1]);    }    a[0] = 0;    for (int i = 1; i < n + 1; i++) a[i] = a[i - 1] + a[i];    while (q--){        int l, r;        cin >> l >> r;        l--;        cout << a[r] - a[l] << ' ';    }    cout << '\n';} signed main(void){    std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     cin >> t;    while (t--) solve();    return 0;}