#include <bits/stdc++.h>using namespace std; void solve() {    int n, a = 0, c = 0;    cin >> n;    int arr[n];    for (int i = 0; i < n; i++){        cin >> arr[i];        a = max (a, arr[i]);    }    for (int i = 0; i < n; i++){        if (arr[i] == a) c++;    }    cout << c << '\n';} signed main(void){        // freopen("input.txt", "r", stdin);    // freopen("output.txt", "w", stdout);        std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     cin >> t;    while (t--) solve();    return 0;    }