#include <bits/stdc++.h>using namespace std; void solve() {    int n, k;    cin >> n;    int arr[n];    for (int i = 0; i < n; i++){        cin >> arr[i];        if (arr[i] == n) k = i;    }    swap (arr[0], arr[k]);    for (int i = 0; i < n; i++) cout << arr[i] << ' ';    cout << '\n';} signed main(void){        // freopen("input.txt", "r", stdin);    // freopen("output.txt", "w", stdout);        std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     cin >> t;    while (t--) solve();    return 0;    }