#include <bits/stdc++.h>using namespace std; void solve() {    int n, c = 1;    cin >> n;    string s;    char p;    cin >> s;    p = s[1];    for (int i = 0; i < n - 1; i++){        cin >> s;        if (s[0] == p) c++;        p = s[1];    }    cout << c << '\n';} signed main(void){        std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     // cin >> t;    while (t--) solve();    return 0;    }