#include <bits/stdc++.h>using namespace std; void solve() {    int n;    cin >> n;    int s = 0, d = 0, arr[n], l = 0, r = n - 1, c = 0;    for (int i = 0; i < n; i++) cin >> arr[i];    while (l <= r){        if (arr[l] > arr[r]){            if (c) d += arr[l];            else s += arr[l];            l++;        }        else{            if (c) d += arr[r];            else s += arr[r];            r--;        }        c = 1 - c;    }    cout << s << ' ' << d << '\n';} signed main(void){        std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     // cin >> t;    while (t--) solve();    return 0;    }