#include <bits/stdc++.h>using namespace std; void solve() {    int n;    cin >> n;    int arr[n], k = 0;    for (int i = 0; i < n; i++){        cin >> arr[i];        if (arr[i] == 1 || arr[i] == n){            k = max (k, i);            k = max (k, n - i - 1);        }    }    cout << k << '\n';} signed main(void){        std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     // cin >> t;    while (t--) solve();    return 0;    }