#include <bits/stdc++.h>using namespace std;void solve() {    string s;    cin >> s;    int a = s[0] - 'a', b = s[1] - '1', c = -1;    for (int i = 0; i < 8; i++){        for (int j = 0; j < 8; j++){            if (abs (i - a) <= 1 && abs (j - b) <= 1) c++;        }    }    cout << c;} signed main(void){        // freopen("input.txt", "r", stdin);    // freopen("output.txt", "w", stdout);        std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     // cin >> t;    while (t--) solve();    return 0;    }