#include <bits/stdc++.h>using namespace std; void solve() {    int n, a = 0;    cin >> n;    string s;    cin >> s;    for (int i = 0; i < n; i++){        if (s[i] == 'A') a++;    }    if (a == n - a) cout << "Friendship\n";    else if (a > n - a) cout << "Anton\n";    else cout << "Danik\n";} signed main(void){        // freopen("input.txt", "r", stdin);    // freopen("output.txt", "w", stdout);        std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     // cin >> t;    while (t--) solve();    return 0;    }