#include <bits/stdc++.h>using namespace std; void solve() {    int n, l = -1, r = -2, k, x;    cin >> n >> k;    for (int i = 0; i < n; i++){        cin >> x;        if (x > k){            if (l < 0) l = i;            r = i;        }    }    cout << n - (r - l + 1) << '\n';} signed main(void){        std::ios::sync_with_stdio(false);    cin.tie(0); cout.tie(0);        int t = 1;     // cin >> t;    while (t--) solve();    return 0;    }